from flask import Flask, requestfrom flask.ext.restful import reqparse, abort, Resource, Apifrom flask.ext.sqlalchemy import SQLAlchemyfrom datetime import datetimeapp = Flask(__name__)app.config['SQLALCHEMY_DATABASE_URI']='mysql://root:suruiqiang@localhost:3306/article'app.config['SQLALCHEMY_TRACK_MODIFICATIONS'] = 'true'db = SQLAlchemy(app)api = Api(app)class Science(db.Model):    __tablename__ = 'science'    #__table_args__ = {'mysql_engine':'InnoDB','mysql_charset':'utf8'}    id = db.Column(db.Integer, primary_key=True, autoincrement=True)    title = db.Column(db.String(100))    link = db.Column(db.String(1024))    image = db.Column(db.String(1024))    summary = db.Column(db.String(1024*10))    def to_json(self):        return {'id':self.id,'title':self.title,'link':self.link, 'image':self.image, 'summary':self.summary}class ScienceList(Resource):    def get(self):        article_list = []        articles = Science.query.limit(100).all()        for n in articles:            article_list.append(n.to_json())        return {'sciences':article_list}api.add_resource(ScienceList,'/api/news/latest')if __name__ == '__main__':    db.create_all()    app.run(debug=True)